declare module "*.scss" {
  const styles: { [className: string]: string };
  export default styles;
}
declare module '*.css'
declare module '*.js'
declare module '*.ttf' {
    const content: string;
    export default content;
  }
declare module '*.svg' {
    const content: string;
    export default content;
  }
declare module '*.jpg' {
    const content: string;
    export default content;
  }